# Create the populations table

CREATE TABLE populations_served (
            population_id INTEGER PRIMARY KEY,
            population_name TEXT UNIQUE
        );

# Fill in populations table


# Create the resource types table

CREATE TABLE resource_types (
            resource_type_id INTEGER PRIMARY KEY,
            resource_type_name TEXT UNIQUE
        );

# Fill in the resource types table


# Create the categories table

CREATE TABLE categories (
    category_id INTEGER PRIMARY KEY,
    category_name TEXT UNIQUE
        );

# Create junction table to link categories to their respective resource types

CREATE TABLE resource_type_categories (
    resource_type_id INTEGER NOT NULL,
    category_id INTEGER NOT NULL,
    PRIMARY KEY (resource_type_id, category_id),
    FOREIGN KEY (resource_type_id) REFERENCES resource_types(resource_type_id),
    FOREIGN KEY (category_id) REFERENCES categories(category_id) );

# Fill in 'resource_type_categories' junction table


# create the resources table

CREATE TABLE resources (
            resource_id INTEGER PRIMARY KEY,
            location_name TEXT,
            organization_name TEXT,
            phone_number TEXT,
            address TEXT,
            city TEXT,
            state TEXT,
            zip_code TEXT,
            website TEXT,
            image TEXT,
            latitude REAL,
            longitude REAL,
            full_address TEXT,
            phone_url TEXT,
            google_maps_url TEXT,
            more_info TEXT
        );

# fill in the resources table


-- Index: idx_resources_city
CREATE INDEX IF NOT EXISTS idx_resources_city ON resources(city);


CREATE TABLE resources_counties (
            resource_id INTEGER,
            county_id INTEGER,
            PRIMARY KEY (resource_id, county_id),
            FOREIGN KEY(resource_id) REFERENCES resources(resource_id),
            FOREIGN KEY(county_id) REFERENCES counties(county_id)
        );

CREATE TABLE resources_populations (
            resource_id INTEGER,
            population_id INTEGER,
            PRIMARY KEY (resource_id, population_id),
            FOREIGN KEY(resource_id) REFERENCES resources(resource_id),
            FOREIGN KEY(population_id) REFERENCES populations_served(population_id)
        );


CREATE TABLE resources_categories (
            resource_id INTEGER,
            category_id INTEGER,
            PRIMARY KEY (resource_id, category_id),
            FOREIGN KEY(resource_id) REFERENCES resources(resource_id),
            FOREIGN KEY(category_id) REFERENCES categories(category_id)
        );

