INSERT INTO resources (resource_id, location_name, organization_name, phone_number, address, city, state, zip_code, website, image, latitude, longitude, full_address, phone_url, google_maps_url, more_info) VALUES (148, 'Danny''s Ride', 'Danny''s Ride', NULL, NULL, NULL, 'PA', NULL, 'https://dannysride.org/', 'https://resourcespage.pages.dev/assets/resource-logos/dannysride.png', 40.3297592, NULL, 'n/a, n/a, PA, n/a', 'tel://+1', 'http://maps.google.com/maps?q=n%2Fa%2C%20n%2Fa%2C%20PA%2C%20n%2Fa', 'Provides (generally no more than two to four) Uber/Lyft rides to individuals in recovery from a substance use disorder. Intended for situations "where there is no other reasonable way for clients to get where they need to go to get essential support on their recovery journey". Rides are to and from outpatient treatment, MAT appointments, AA and NA meetings, legal system meetings, employment related rides and more. Riders must go through the staff of the organization or agency they are recieving services from, as riders may not arrange their own rides or change ride details.');
